import { useEffect, useState } from "react";
import "./App.css";
import Snake from "./Snake";
import Food from "./Food";

const [lastDirection, setLastDirection] = useState({ val: "" });

const getDirection = (e) => {
	switch (e.keyCode) {
		case 38:
			return "UP";
			setLastDirection({ val: "UP" });
		case 40:
			return "DOWN";
			setLastDirection({ val: "DOWN" });
		case 37:
			return "LEFT";
			setLastDirection({ val: "LEFT" });
		case 39:
			return "RIGHT";
			setLastDirection({ val: "RIGHT" });
		default:
			return "";
	}
};

function App() {
	const getRandomFoodDot = () => {
		let min = 1;
		let max = 98;

		let x = Math.floor((Math.random() * (max - min + 1) + min) / 2) * 2;
		let y = Math.floor((Math.random() * (max - min + 1) + min) / 2) * 2;

		return [x, y];
	};

	const [snakeDots, setSnakeDots] = useState([
		[0, 0],
		[2, 0],
	]);
	const [foodDots, setFoodDots] = useState(getRandomFoodDot());
	const [direction, setDirection] = useState({ val: "" });

	const moveSnake = () => {
		let dots = [...snakeDots];
		let head = dots[dots.length - 1];
		switch (direction.val) {
			case "UP":
				head = [head[0], head[1] - 2];
				dots.push(head);
				dots.shift();
				break;
			case "DOWN":
				head = [head[0], head[1] + 2];
				dots.push(head);
				dots.shift();
				break;
			case "LEFT":
				head = [head[0] - 2, head[1]];
				dots.push(head);
				dots.shift();
				break;
			case "RIGHT":
				head = [head[0] + 2, head[1]];
				dots.push(head);
				dots.shift();
				break;
			default:
				break;
		}
		setSnakeDots(dots);
	};

	useEffect(() => {
		const interval = setInterval(() => {
			moveSnake();
		}, 200);
		return () => clearInterval(interval);
	}, [snakeDots]);

	useEffect(() => {
		const keyPressHandler = (e) => {
			setDirection({ val: getDirection(e) });
		};
		document.addEventListener("keydown", keyPressHandler);
		return () => {
			document.removeEventListener("keydown", keyPressHandler);
		};
	}, []);

	return (
		<div className="app">
			<div className="snake">
				<Snake snakeDots={snakeDots} />
				<Food foodDots={foodDots} />
			</div>
		</div>
	);
}

export default App;
