import { useState } from "react";
import "./App.css";
import Snake from "./Snake";
import Food from "./Food";

function App() {
	const getRandomFoodDot = () => {
		let min = 1;
		let max = 98;

		let x = Math.floor((Math.random() * (max - min + 1) + min) / 2) * 2;
		let y = Math.floor((Math.random() * (max - min + 1) + min) / 2) * 2;

		return [x, y];
	};

	const [snakeDots, setSnakeDots] = useState([
		[0, 0],
		[2, 0],
	]);
	const [foodDots, setFoodDots] = useState(getRandomFoodDot());
	const [direction, setDirection] = useState("RIGHT");

	// const handleKey = (e) => {
	// 	onkeydown(setDirection("test"));
	// };

	// onKeyDown(setDirection("test"));
	// console.log(snakeDots);

	window.addEventListener("keydown", checkKeyPress, false);

	const checkKeyPress = (e) => {
		if (e.keyCode == "65") {
			alert("THe 'a' was pressed");
		}
	};

	return (
		<div onKeyDown={handleKeyDown} className="app">
			<div className="snake">
				<Snake snakeDots={snakeDots} />
				<Food foodDots={foodDots} />
				<input onKeyDown={handleKeyDown} />
			</div>
		</div>
	);
}

export default App;
